  }
  #notebook .alert-success {
    color: var(--v4-9) !important;
    border-color: var(--v4-2) !important;
    background-color: var(--v4-1) !important;
  }
  #notebook .alert-info {
    color: var(--v6-9) !important;
    border-color: var(--v6-2) !important;
    background-color: var(--v6-1) !important;
  }
  #notebook .o,
  #notebook .mi,
  #notebook .mf {
