  #notebook .bp {
    color: var(--v4-7) !important;
  }
  #notebook .nn,
  #notebook .nf,
  #notebook .nc {
    color: var(--v6-7) !important;
  }
  #notebook .c1 {
    color: var(--v5-7) !important;
  }
  #notebook .s1,
  #notebook .s2 {
    color: var(--v1-7) !important;
  }
  #notebook .ow {
    color: var(--v8-7) !important;
  }

