/* 1.1.0
- Added better Stat Chips
- Fixed stuff
- More stuff probably
- I forget most of what I did.
- Probably more bugs.
*/


  .btn {
    border: 0;
  }
  :root {
    --bg-1: #261226;
    --fg-1: #ddd;
  }
  span[class="fg:4 my:0"] {
    color: var(--fg-1);
    opacity: 0.7;
  }
  * {
    border-color: rgba(221,221,221,0.1) !important;
  }
  code,
  textarea,
  form {
    background-color: var(--bg-1) !important;
    filter: brightness(1.2);
  }
  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    color: var(--fg-1) !important;
  }
  body > nav {
    position: sticky;
    top: -1.7em;
    z-index: 100;
    min-height: 70px;
    display: flex;
    padding-top: 16px !important;
    border-bottom: 1px solid var(--bg-2);
  }
  body > nav > .wrapper {
    margin: 0 auto !important;
    height: min-content;
    padding-bottom: 0;
    padding-top: 0;
    margin-bottom: -3em;
    position: sticky;
    top: 10px;
    min-height: 40px;
    align-items: center;
  }
  body > nav > .wrapper .mascot {
    height: 50px !important;
    margin-top: -5px;
