/* ==UserStyle==
@name           Userstyles.World Tweaks!
@version        1.1.0
@description    Tweaks for Userstyles.World!
@updateURL      https://codeberg.org/Freeplay/UserStyles/raw/branch/main/Userstyles.world_tweaks.user.css
@preprocessor   stylus

 -- AUTHOR STUFF --
@namespace      Freeplay
@author         Freeplay (https://freeplay.codeberg.page/)
@homepageURL    https://codeberg.org/Freeplay/UserStyles
@supportURL     https://codeberg.org/Freeplay/UserStyles/issues

 -- VARS --
@var checkbox fixedHeader "Fixed Header" 1

@var checkbox pillHeader "Pill header style (Disabled on mobile)" 1

@var checkbox blurEffects "Blur Effects" 1

@var select   widthType "Width type" ["Regular", "Full-Width", "Responsive*"]

@var checkbox altCards "Alternative Cards" 1

@var checkbox uncrop "Uncrop image previews on style page" 1

@var checkbox altHeaders "Alternative Header Layout" 1

@var checkbox betterChips "Better stat chips" 1

@var checkbox gradient "Better Headers" 1
@var checkbox gradientUnderline "Gradient Underlines" 1

@var checkbox enableColorScheme "Use a custom Color Scheme (slightly scuffed)" 0
@var color colorScheme "Color Scheme" #101010
@var color colorText "Color Text" #ddd

==/UserStyle== */
/* 1.1.0
- Added better Stat Chips
- Fixed stuff
- More stuff probably
- I forget most of what I did.
- Probably more bugs.
*/
@-moz-document domain("userstyles.world") {
	.btn {
		border: 0;
	}
	if enableColorScheme {
		:root {
			--bg-1: colorScheme;
			--fg-1: colorText;
		}
		span[class="fg:4 my:0"] {
			color: var(--fg-1);
			opacity: .7;
		}
		* {
			border-color: rgba( colorText, .1 ) !important;
		}
		code, textarea, form {
			background-color: var(--bg-1) !important;
			filter: brightness(1.2);
		}
		h1, h2, h3, h4, h5, h6 {
			color: var(--fg-1) !important;
		}
	}
	if fixedHeader {
		body > nav {
			position: sticky;
			top: -1.7em;
			z-index: 100;
			min-height: 70px;
			display: flex;
			padding-top: 16px !important;
			border-bottom: 1px solid var(--bg-2);
			& > .wrapper {
				margin: 0 auto !important;
				height: min-content;
				padding-bottom: 0;
				padding-top: 0;
				margin-bottom: -3em;
				position: sticky;
				top: 10px;
				min-height: 40px;
				align-items: center;
				// align-items: center;
				.mascot {
					height: 50px !important;
					margin-top: -5px;
					margin-bottom: -5px;
					margin-right: 0;
					padding: 0 !important;
				}
				.mascot svg {
					position: sticky;
					top: 12.5px;	
					margin-right: 5px;
				}
				.mascot span {
					font-weight: 800;
					font-size: 1.2em;
				}
				.menu-icon {
					top: unset;
					bottom: 18px !important;
					// height: 100% !important;
				}
				.menu {
					flex-grow: 1;
					padding-top: 0;
					padding-bottom: 0;
					width: 100%;
					li:first-child {
						flex-grow: 1;
						margin: 0 20px;
						.search {
							flex-grow: 1;
							// max-height: 100% !important;
							margin: 0;
							width: 100%;
							max-width: 550px !important;
							margin: auto;
							input {
								background: none !important;
								width: 100%;
								border: 0;
								border-radius: 0;
								height: 42px;
								padding-left: 18px;
								box-shadow: none !important;
								&::placeholder {
									opacity: .8;
								}
							}
							button {
								height: unset !important;
								background: none;
								right: 8px;
							}
							&::before {
								content: "";
								position: absolute;
								top: 0;
								left: 0;
								right: 0;
								bottom: 0;
								background: var(--bg-3);
								z-index: -1;
								opacity: .5;
								border-radius: 8px;
								transition: opacity .2s;
							}
							&:hover:before {
								opacity: .6;
							}
							&:focus-within:before {
								opacity: .8;
							}
							&:focus-within input::placeholder {
								opacity: .7 !important;
							}
						}
					}
					li {
						height: auto !important;
						min-height: 35px !important;
						flex-direction: column;
						a, input {
							width: 80%;
							flex-grow: 1;
							display: flex !important;
							align-items: center !important;
							white-space: nowrap !important;
							font-weight: 500;
						}
					}
				}

			}
			if blurEffects && !pillHeader {
				background: none !important;
				backdrop-filter: blur(10px);
				&::before {
					content: "";
					position: absolute;
					top: 0;
					left: 0;
					right: 0;
					bottom: 0;
					background-color: var(--bg-1);
					filter: brightness(2);
					z-index: -1;
					opacity: 0.3;
				}
			}
		}
	}
	if pillHeader {
		@media only screen and (min-width: 769px) {
			body > nav {
				background: none;
				border: 0;
				pointer-events: none !important;
				.wrapper {
					max-width: 100rem;
					padding: 30px 40px !important;
					.mascot, .menu {
						pointer-events: all !important;
						margin-top: 0 !important;
						background: var(--bg-2);
						padding: 0 20px !important;
						border-radius: 32px;
						height: 35px !important;
						position: relative;
						&.mascot {
							margin-right: auto;
							margin-bottom: 0 !important;
						}
						&.menu {
							width: max-content !important;
							flex: unset !important;
							padding-left: 0 !important;
							margin-left: 20px;
							li {
								margin-bottom: -1px !important;
							}
							li:first-child {
								margin: 0;
								width: 250px;
								transition: width .3s;
								form {
									display: flex;
									flex-direction: column;
									min-width: 100%;
									flex-grow: 1;
									&::before {
										content: none !important;
									}
								}
								input {
									border: 0;
									padding: 0 20px;
									width: 100% !important;
									transition: width .3s;
									height: 100%;
									box-shadow: none !important;
									outline: none !important;
									&::placeholder {
										color: var(--fg-1) !important;
										opacity: 1;
										transition: opacity .5s;
									}
								}
								button {
									border: 0;
									margin-bottom: -1px;
								}
								&:focus-within {
									width: 500px;
									button {
										display: block;
									}
									input::placeholder {
										color: var(--fg-1) !important;
										opacity: .5;
									}
								}
							}
						}

						li:first-child a {
							margin-left: 0;
						}
						a::before {
							bottom: 0px !important;
						}
						a:hover:before {
							height: 4px !important;

						}
						if blurEffects {
							background: none;
							backdrop-filter: blur(10px);
							&::before {
								content: "";
								position: absolute;
								top: 0;
								left: 0;
								right: 0;
								bottom: 0;
								background-color: var(--bg-2);
								border-radius: 32px;
								opacity: .5;
							}
						}
					}
				}

			}
		}
	}

	body > nav > .wrapper > ul li {
		height: 100%;
		display: flex;
		a, button {
			height: 100% !important;
			display: flex;
			align-items: center;
			&::before {
				bottom: -2.1px !important;
			}
			&:hover, &:focus {
				filter: brightness(1.5) !important;
			}
		}
	}


	if widthType == "Full-Width" || widthType == "Responsive" {
		// ewie bad selectors
		.wrapper > section[class="mt:m ta:c"] + section[class="mt:l"],
 		.wrapper > section[class="mt:l"] + section[class="mt:l"] {
			display: flex;
			// flex-direction: column;
			align-content: center;
			justify-content: center;
			& > .grid {
				if widthType == "Full-Width" {
					min-width: calc(100vw - 80px);
					display: grid;
					grid-template-columns: repeat(auto-fill,minmax(250px,1fr));
				}
				if widthType == "Responsive" {
					min-width: calc(100vw - 80px);
					display: grid;
					grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
					padding: 0 calc(15vw - 100px);
				}
				.card {
					min-width: unset !important;
				}
			}
		}
	}

	html body > * > .wrapper {
		margin: 0 auto;
	}
	if altCards {
		.card {
			background: none;
			border: 0;
			.thumbnail {
				border-radius: 8px !important;
				overflow: hidden;
			}
			& > div.flex {
				border: 0;
				padding: 10px 0;
			}
			&.animate {
				transition: transform .2s;
				figure {
					transition: transform .2s;
					img {
						transition: transform .7s;
					}

					&::before {
						background-color: rgba(0,0,0,0.8) !important;
						transition: opacity .5s 0s !important;
					}
					&::after {
						color: #eee !important;
						transform: scale(1.04);
						transition: .2s 0s;
					}
				}
			}
			&.animate:hover, &.animate:focus {
				transform: translatey(-1px) !important;
				figure {
					transform: scale(1.02);
					img {
						transform: scale(1.02);
					}
					&::before {
						transition: opacity .5s .5s !important;
					}
					&::after {
						transform: none !important;
						transition: .5s .5s;
					}
				}
			}
		}
	}
	if uncrop {
		.preview .card picture {
			padding: 0 !important;
			background-color: var(--bg-2);
			border-radius: 8px;
			overflow: hidden;
			max-height: 75vh;
			img {
				position: relative !important;
				max-height: 75vh !important;
				object-fit: contain !important;
				margin: 0 !important;
			}
		}
	}


	if altHeaders {
		[class*="mt:m ta:c"] {
			text-align: left !important;
			display: flex !important;
			align-content: center;
			align-items: flex-end;
			margin: 0;
			justify-content: space-between;
			p {
				margin-bottom: .7em !important;
				margin-left: 1em !important;
			}
		}
		[class*="ta:c"] {
			text-align: left;
		}
		form, .err {
			margin: 1rem 0;
		}
	}
	if gradient {
		.motto {
			font-size: 1.3rem;
			font-weight: 600;
		}
		h1, .motto {
			background: -webkit-linear-gradient(245deg, #ffc400, #ff4000) !important;

			-webkit-background-clip: text !important;
			-webkit-text-fill-color: transparent !important;
		}
		h2 {
			text-decoration: none !important;
			border-bottom: 1px solid var(--bg-3);
			font-size: 1.2em;
		}
	}
	if gradientUnderline {
		@keyframes underlineAnimate {
			0% { width: 0%; left: 0; }
			100% { width: 100%; left: 0; }
		}
		a:not(.mascot):not(.card):not(.btn), a.mascot span, .link {
			position: relative;
			text-decoration: none !important;
			outline: none !important;
			&::before {
				content: "";
				width: 0%;
				height: 0px;
				right: 0;
				transition: right .3s, width .3s, height .2s !important;
				background: -webkit-linear-gradient(245deg, #ffc400, #ff4000);
				position: absolute;
				bottom: -.3em;
				border-radius: 100px;
			}
			&:hover:before, &:focus:before {
				content: "";
				width: 100%;
				height: .2em;
				animation: underlineAnimate .3s !important;

			}
		}
	}

	if betterChips {
		.details, .stats, .settings, .welcome, .socials {
			display: flex !important;
			flex-wrap: wrap;
			margin-top: 0;
			[class*="mt:m"], h1, h2, .md, form {
				width: 100%;
			}
			.sub-title {
				// display: none;
			}
		}
		.welcome > h1:first-child {
			margin-bottom: 6px;
		}
		.stats, .socials {
			& > h2 {
				display: none;
			}
		}
		.stats + [class="mt:m"] {
			margin-top: 0;
		}
		.welcome > h2[class="sub-title td:d mt:l"] {
			// display: none;
			margin-top: 8px;
			& + .md {
				// margin-top: 10px;
			}
		}
		.details  > p,
		.stats    > p,
		.settings > p,
		.socials > div,
 		.id, .role, .joined {
			background: var(--bg-2);
			border-radius: 16px;
			// height: min-content;
			margin-bottom: 8px;
			margin-right: 8px;
			padding: 4px 16px;
			color: var(--fg-3);
			display: flex;
			// flex-direction: column;
			// grid-gap: 10px;
			position: relative;
			overflow: hidden;
			width: auto !important;
			span {
				min-width: unset;
				color: var(--fg-1);
				margin-right: 8px;
			}
			a {
				position: static !important;
				&::after {
					content: "";
					position: absolute;
					top: 0;
					left: 0;
					right: 0;
					bottom: 0;
					transition: background .2s;
				}
				&::before {
					content: none !important;
				}
				&:hover:after {
					background: rgba(0,0,0,0.1);
				}
			}
		}
		.socials > div {
			border-radius: 6px;
			padding: 6px 12px;
		}
	}


	p[class="ta:c mt:m mb:0"] {
		position: sticky;
		bottom: 0;
		background-color: var(--bg-1);
		border-radius: 12px 12px;
		padding: 6px 15px;
		text-align: center;
		z-index: 2;
		margin-top: 40px !important;
		bottom: 10px;
		width: max-content;
		margin: auto;
		a {
			position: static !important;
		}
		a::before {
			bottom: -10px !important;
		}
		a::after {
			content: "";
			position: absolute;
			top: 0;
			left: 0;
			right: 0;
			bottom: 0;
			z-index: 3;
		}
		if blurEffects {
			background: none !important;
			backdrop-filter: blur(10px);

			&::before {
				content: "";
				position: absolute;
				top: 0;
				left: 0;
				right: 0;
				bottom: 0;
				background-color: var(--bg-2);
				border-radius: 32px;
				opacity: .7;
				z-index: -1;
			}
		}
	}


	footer {
		background: none;
	}
}


	// Uncomment below to torture Gopher :)
/*
		@keyframes uwu {
			from { transform: rotateZ(0deg) skewX(0deg);
				   filter: hue-Rotate(0deg)                }
			to {   transform: rotateZ(360deg) skewX(360deg);
				   filter: hue-Rotate(360deg) }
		}
		nav > .wrapper {
			overflow: hidden;
		}
		.mascot svg {
			animation: uwu 20s linear 1s infinite !important;
		}

*/

