:root {
    --dark: #292929;
    --lighter: #bababa;
  }
  body {
    color: var(--lighter);
    background-color: var(--dark);
  }
  :root {
    --dark: #292929;
    --darken: #333;
    --darker: #424242;
    --light: #e6e6e6;
    --lighter: #bababa;
    --accent: #bf692b;
    --shadow: rgba(0,0,0,0.145);
    --yellow: #f0c674;
    --orange: #d98245;
