    border: 1px solid rgba(0,0,0,0.1);
    margin: -8px 0;
    text-decoration: none;
    margin-right: 8px;
  }
  #stylus-popup > .actions #write-style {
    display: flex;
    flex-wrap: wrap;
  }
  #stylus-popup > .actions #write-style #write-style-for {
    font-size: 16px;
    font-weight: 1000;
    padding-bottom: 4px;
  }
  #stylus-popup > .actions #write-style .match {
    width: 100%;
  }
  #stylus-popup > .actions #write-style .match span a {
    text-decoration: none;
    font-size: 13px;
  }
  #stylus-popup > .actions #write-style #write-for-frames {
    position: relative;
    margin: 6.5px;
    margin-left: 0;
    margin-right: 10px;
  }
  #stylus-popup > .actions #write-style #write-for-frames::after {
    border-radius: 100%;
    margin: -4px;
  }
  #stylus-popup > .actions #popup-options {
    padding: 16px;
    padding-top: 12px;
    padding-top: 0;
    padding-bottom: 0;
  }
  #stylus-popup #search-results {
    margin-left: auto !important;
    margin-right: auto !important;
    width: 100%;
    order: 3;
    padding-top: 15px;
    background-color: inherit;
  }
  #stylus-popup #search-results #search-params {
    padding: 8px 4px;
    margin-bottom: 8px;
  }
  #stylus-popup #search-results #search-params > * {
