  }
  #stylus-popup #search-results #search-params > label {
    margin-left: 10px !important;
    align-items: center;
    display: flex;
  }
  #stylus-popup #search-results .search-results-nav {
    position: sticky;
    bottom: 0;
    background: inherit;
  }
  #stylus-popup #search-results #search-results-list {
    display: flex;
    flex-wrap: wrap;
    margin: 0 6px;
    justify-content: center;
  }
  #stylus-popup #search-results #search-results-list .search-result {
    box-shadow: none;
    border: none;
    width: 360px;
    padding: 8px;
    margin: 0;
    border-radius: 6px;
    padding-bottom: 25px;
    background-color: transparent;
  }
  #stylus-popup #search-results #search-results-list .search-result .search-result-title {
    text-decoration: none;
  }
  #stylus-popup #search-results #search-results-list .search-result .search-result-title span {
    font-size: 14px !important;
  }
  #stylus-popup #search-results #search-results-list .search-result .search-result-meta {
    background-color: rgba(237,237,237,0.2);
  }
  #stylus-popup #search-results #search-results-list .search-result:hover {
    background-color: rgba(38,115,115,0.1);
  }
  #stylus-popup #search-results:not([data-empty])::before {
    filter: opacity(0);
  }
  #stylus-popup #search-results-error {
    width: 100%;
  }

