#stylus-edit #header {
    position: fixed !important;
    right: 0 !important;
    top: 0 !important;
    margin: 15px;
    height: auto !important;
    box-sizing: border-box;
    border-radius: 8px;
    opacity: 0.8;
    transform: scale(0.98);
    box-shadow: 0 2px 8px rgba(0,0,0,0.2) !important;
    width: 30vw;
    min-width: 250px;
    max-width: 300px;
    transition: opacity 0.3s, transform 0.5s, max-height 0.3s, max-width 0.2s;
    overflow-y: scroll;
    flex-direction: column !important;
    backdrop-filter: blur(10px);
    background-color: rgba(250,250,250,0.5);
  }
  #stylus-edit #header > :not(#basic-info) {
    max-height: 0;
    overflow: hidden;
    transition: max-height 0.2s;
  }
  #stylus-edit #basic-info {
    margin-bottom: 0;
  }
  #stylus-edit #header:hover {
    opacity: 1;
    transform: scale(1);
    max-height: calc(100vh - 30px) !important;
  }
  #stylus-edit #header:hover > :not(#basic-info) {
    max-height: calc(100vh - 30px);
    overflow: visible;
    flex-wrap: nowrap;
  }
  #actions > div {
    margin-top: 1rem;
  }
  #stylus-edit #details-wrapper {
    flex-direction: column !important;
  }
  #stylus-edit #heading {
    display: none;
  }
  #stylus-edit #sections {
    padding-left: 0 !important;
  }
  #stylus-edit .CodeMirror-scroll {
    max-height: 100vh !important;
  }
  #stylus-edit .CodeMirror pre.CodeMirror-line > span,
  #stylus-edit .CodeMirror pre.CodeMirror-line-like > span {
    padding-right: 400px !important;
    box-sizing: content-box;
  }

