	#actions > div {
		margin-top: 1rem;
	}
	#stylus-edit #details-wrapper {
		flex-direction: column !important;
	}
	#stylus-edit #heading {
		display: none;
	}
	#stylus-edit #sections {
		padding-left: 0 !important;
	}
	#stylus-edit .CodeMirror-scroll {
		max-height: 100vh !important;
	}
	/* Add right padding due to width of popup */
	#stylus-edit .CodeMirror pre.CodeMirror-line > span, 
	#stylus-edit .CodeMirror pre.CodeMirror-line-like > span {
		padding-right: 400px !important;
		box-sizing: content-box;		
	}

}
