sourceCode:
/* ==UserStyle==
@name           YouTube Studio Dark Theme
@namespace      USO Archive
@author         Lexan
@description    `This theme is an edit of a Dark Mode theme made by Leon Flame, please be sure to check out their theme here: https://userstyles.org/styles/185017/youtube-studio-vidiq-darkIf you would like to check out my channel, it can be found here: https://www.youtube.com/daniel55555d`
@version        202121.22.56
@license        CC-BY-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("studio.youtube.com") {
/* Dark Creator Studio v6.8*/
/* Created by Leon Flame, edited by Lexan*/

/* Dark Scrollbar*/

::-webkit-scrollbar,
 ::-webkit-scrollbar-corner,
 ::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #222 !important;
  }
::-webkit-scrollbar-button,
 ::-webkit-scrollbar-button {
    display: none !important;
  }
::-webkit-scrollbar-thumb,
 ::-webkit-scrollbar-thumb {
    background: #444 !important;
  }
::-webkit-scrollbar-thumb:not(:active):hover,
 ::-webkit-scrollbar-thumb:not(:active):hover {
    background: #383838 !important;
  }
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active,
 ::-webkit-scrollbar-thumb:active,
 ::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c !important;
  }

/* vars */

:root {
    	--header-color: #222;
        --accent-color: #FF2222;
		--primary-color: var(--theme-color) !important;
		--ytve-border-color-focused: var(--theme-color) !important;
		--ytve-timeline-marker-color-active: var(--theme-color) !important;
		--ytcp-focused-color: var(--theme-color) !important;
		--theme-darken-color: #111;
		--theme-add-color: #111;
		--theme-darken2-color: #800;
		--theme-darken3-color: #000;
		--theme-lighten-color: #f77;
		--theme-lighten2-color: #fbb;
		--shadow: 0px 0px 7px 4px #0006;
		--bg-color: #0F0F0F;
		--ytcp-background-10: #111 !important;
		--bg2-color: #141414;
		--bg3-color: #222;
		--item-color: #333;
		--item-hover-color: #666;
		--item-active-color: #777;
    	--divider-color: #bbb;
    	--border-color: #1d1d1d;
		--text-color: #fff;
		--text-darken-color: #ddd;
		--text-darken2-color: #aaa;
        --ytve-secondary-color: #aaa !important;
        --ytcp-focused-background-color: #333 !important;
    	--ytcp-studio-green: #009900;
    	--green-color: #00BE07;
    --paper-button-flat-keyboard-focus_-_background-color: #f77 !important;
    --ytcp-call-to-action: var(--theme-color) !important;
    --ytcp-text-primary: #fff !important;
	--ytve-background-color: #333 !important;
    --ytcp-highlighted-background-color: #666 !important;
    --paper-tabs-selection-bar-color: var(--theme-color) !important;
    --ytcp-entity-card-title-color: #fff;
    --yt-spec-text-primary: #fff !important;
    --paper-progress-active-color: #f00 !important;
    --paper-tab-ink: var(--theme-color) !important;
    --ytcp-link-color: var(--theme-color) !important;
    --paper-progress-container-color: #f44 !important;
    --yt-spec-call-to-action: var(--theme-color) !important;
    --ytcp-black-disabled: #ccc !important;
    --ytcp-background-0: #111 !important;
    --ytcp-call-to-action-ripple: #eee !important;
}


/*---------------------------------------------------------NEW CODE----------------------------------------------------------------*/

/*Main header and toolbar*/
body {
    background: var(--bg-color);
}

#main.ytcp-entity-page{
    background: var(--bg-color);
}

ytcp-navigation-drawer.ytcp-entity-page{
    background-color: var(--bg-color);
}

.ytcp-header {
    background: var(--header-color);
}

header.ytcp-header{
    box-shadow: rgba(15, 15, 15, 0.5) 0px 0px 0px 1px, rgba(15, 15, 15, 0.5) 0px 4px 4px;
}

#create-icon{
    background-color: #444;
    border-color: var(--item-color) !important;
    box-shadow: rgba(15, 15, 15, 0.5) 0px 0px 0px 1px, rgba(15, 15, 15, 0.5) 0px 4px 4px;
}

#create-icon:hover{
    background-color: #a00;
    transition: .3s;
}

#create-icon iron-icon svg g path{
	fill: var(--accent-color);
}

.st0.style-scope.ytcp-home-button, iron-icon[icon="icons:search"]{
    fill: var(--text-color);
}

.st1.style-scope.ytcp-home-button {
    fill: var(--text-color) !important;
}

.st2.style-scope.ytcp-home-button {
    fill: var(--accent-color);
}

.ytcp-icon-button {
    fill: var(--text-color);
}

#query-input {
    background: rgba(255,255,255,0.2);  
    color: var(--text-color);
}

#search-layer, #query-input{
    border: none !important;
    background-color: var(--theme-add-color);
    opacity: 1 !important;
    border-radius: 5px;
}

#query-input::placeholder {
    color: var(--text-darken2-color);
    opacity: 1;
}

.search-icon.ytcp-omnisearch {
    color: var(--text-color) !important;
}

ytcp-navigation-drawer, #left-nav{
    background: #1a1a1a;
    color: var(--text-color);
}

.nav-item-text{
    color: var(--text-color);
}

iron-icon.ytcp-navigation-drawer {
    --iron-icon-width: 24px;
    --iron-icon-height: 24px;
    color: var(--text-color);
}

paper-icon-item.iron-selected.ytcp-navigation-drawer, paper-icon-item.iron-selected.ytcp-navigation-drawer iron-icon.ytcp-navigation-drawer {
    color: var(--accent-color);    
}

paper-icon-item.iron-selected.ytcp-navigation-drawer > .nav-item-text {
    color: var(--accent-color);   
}

.menu-item-link.ytcp-navigation-drawer > paper-icon-item:hover > #contentIcon > iron-icon.ytcp-navigation-drawer {
    transition: .09s;
    color: var(--accent-color);
}

.menu-item-link.ytcp-navigation-drawer > paper-icon-item:hover > div {
    transition: .09s;
    color: var(--accent-color);
}

ytcp-navigation-drawer[keyboard-focus] .menu-item-link.ytcp-navigation-drawer:focus paper-icon-item.ytcp-navigation-drawer, ytcp-navigation-drawer[keyboard-focus] paper-icon-item.ytcp-navigation-drawer:focus, paper-icon-item.ytcp-navigation-drawer:hover, paper-icon-item[submenu-open].ytcp-navigation-drawer, paper-icon-item.iron-selected.ytcp-navigation-drawer {
    background: var(--item-color);
    transition: .09s;
}

ytcp-navigation-drawer[enable-matterhorn-entity-label] .entity-type.ytcp-navigation-drawer {
    color:var(--text-color);
}

ytcp-navigation-drawer[enable-matterhorn-entity-label] .entity-name.ytcp-navigation-drawer {
    color:var(--text-color);
}

main {
    background: var(--bg-color) !important;
}

.page-title {
    color: var(--text-color);
}

#create-ocpm.style-scope.ytcp-header{
    color: var(--text-color);
}

#page-title-container.page-title-container.style-scope.ytcp-app{
    background-color: var(--bg2-color);
    padding-bottom: 10px;
}

#backdrop, ytcp-ve, .ytcp-omnisearch{
    border: 1xp solid var(--border-color) !important;
}

#bottom-section > ul > li > ytcp-send-feedback-button > ytcp-ve > paper-icon-item:hover > #contentIcon > iron-icon {
    color: var(--accent-color) !important;
}

#bottom-section > ul > li > ytcp-send-feedback-button > ytcp-ve > paper-icon-item:hover > div {
    color: var(--accent-color) !important;
}

#bottom-section > ul > li > ytcp-ve > paper-icon-item:hover > #contentIcon > iron-icon {
    color: var(--accent-color) !important;
}

#bottom-section > ul > li > ytcp-ve > paper-icon-item:hover > div {
    color: var(--accent-color) !important;
}

#bottom-section > ul > li > ytcp-ve > paper-icon-item:hover > iron-icon {
    color: var(--accent-color) !important;
}

#bottom-section{
    box-shadow: none;
}

#scrim.yta-screen, #scrim.yta-explore-page{ /*loading screen overlay*/
    background-color: #000;
}

/*Footer*/
.footer{
    background-color: var(--bg2-color);
    color: var(--text-darken2-color) !important;
}

.external-link.ytcd-external-link{
    color: var(--text-darken2-color);
}

.external-link-icon.ytcd-external-link, .external-link.ytcd-external-link:hover{
    color: var(--accent-color); 
    transition: .1s;
}

/*Account Switcher*/
.ytd-account-item-section-header-renderer{
    color: var(--text-color) !important;
}

#channel-title{
    color: var(--text-color) !important;
    position: relative;
}

.ytd-account-item-renderer[secondary]{
    color: #bbb !important;
    position: relative;
}

paper-icon-item.ytd-account-item-renderer:hover, paper-item.ytd-compact-link-renderer:hover{
    background-color: var(--bg3-color) !important;
}

/*Universal - Dialog Boxes*/
.header-content.ytcp-dialog > h1[slot='primary-header'], .header-content.ytcp-dialog > h2[slot='primary-header'], .header-content.ytcp-dialog > h3[slot='primary-header'] {
    color: var(--text-color) !important;
}

/*Universal - Action Bar*/
ytcp-primary-action-bar{
    border-bottom: 1px solid var(--border-color);
}

/*Universal - Listboxes*/
paper-listbox{
    background-color: var(--bg2-color) !important;
    color: var(--text-color);
    border: 1px solid var(--border-color);
    border-radius: 6px;
}

paper-listbox paper-item:hover{
    background-color: var(--bg3-color) !important;
}

.paper-listbox-item::before paper-item.ytcp-text-menu:focus{
    background-color: #444 !important;
}

ytd-menu-popup-renderer{
    background-color: #0000;
}

ytcp-chip-bar > div > input::placeholder {
    color: var(--text-darken2-color) !important;
}

ytcp-chip-bar > div > input {
    color: var(--text-color) !important;
}

ytcp-chip > ytcp-icon-button > .ytcp-icon-button {
    fill: var(--accent-color) !important;
}

ytcp-chip {
    background: var(--item-color) !important;
}

ytcp-chip > .text {
    color: var(--text-color) !important;
}

.label-text.ytcp-dropdown-trigger{
    color: var(--text-darken2-color) !important;
}

/*Universal - Radio Buttons*/
#radioLabel.paper-radio-button {
    color:var(--text-color) !important;
}

#offRadio.paper-radio-button {
    border: 2px solid var(--text-color, var(--accent-color));
}

paper-radio-button[checked] #offRadio.paper-radio-button{
    background: var(--accent-color);
    border: 2px solid var(--accent-color, var(--accent-color));
}

#onRadio.paper-radio-button{
    background-color: var(--text-color);
}

/*Universal - Callouts*/
#callout.ytcp-feature-discovery-callout, #divot.ytcp-feature-discovery-callout{
    background-color: var(--item-color);
    border-color: var(--item-color);
}

/*Universal - Text Fields*/
yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string:visited{
    color: #3278FF !important;
}

.text-content *{
    color: var(--text-color) !important;
}

#outer.ytcp-form-input-container {
    background-color: var(--bg3-color);
}

#outer.ytcp-form-input-container:hover{
    border-color: var(--item-color);
    transition: .1s;
}

ytcp-form-input-container.focused #outer.ytcp-form-input-container, ytcp-form-input-container.focused #outer.ytcp-form-input-container:hover{
    border-color: var(--item-active-color) !important;
    transition: .1s;
}

#section-label.ytcp-form-textarea {
   color: var(--text-color) !important;  
}

#section-label.ytcp-form-textarea > div > ytcp-ve > iron_icon {
   fill: var(--text-color) !important;  
}

.text-container.ytcp-form-textarea{
    background-color: var(--bg3-color);
    transition: .1s;
    caret-color: var(--accent-color);
}

.text-container.ytcp-form-textarea:hover{
    border: 1px solid var(--item-color);
    transition: .1s;
    caret-color: var(--accent-color);
}

.focused.text-container.ytcp-form-textarea{
    border-color: var(--item-active-color) !important;
    transition: .1s;
    caret-color: var(--accent-color);
}

.no-items-message.ytcp-checkbox-group, input{
    color: var(--text-darken2-color);
}

ytcp-chip-bar > div > input{
    color: var(--text-darken2-color);
}

#description.ytcp-form-input-container > *, #error-message.ytcp-form-input-container {
    color:var(--text-darken2-color) !important;
}

#outer.ytcp-form-input-container > div{
    color: var(--text-darken2-color) !important;
}

.text-input.ytcp-chip-bar{
    caret-color: var(--accent-color);
}

/*Read-only Text fields*/
.readonly-field.ytcp-channel-url{
    background-color: var(--bg3-color);
}

/*Ripple Effects*/
paper-ripple.paper-tab{
    color: var(--accent-color);
}

.wave.style-scope.paper-ripple{
    opacity: 0.3 !important;
}

/*Dashboard*/
.ytcd-item > div > .item-title {
    color: var(--text-color);
}

.ytcd-item > div > div > div {
    color: var(--text-color) !important;
}

#metrics-table > div > div {
    color: var(--text-color) !important;
}

.ytcd-item > div > .section-title > span {
    color: var(--text-color) !important;
}

.ytcd-item > div > .top-videos > table > tbody > tr > td {
    color: var(--text-darken-color) !important;
}

.default-card.ytcd-card > .card-title {
    color: var(--text-color);
}

.default-card.ytcd-card > ytcd-item > ytcd-ki-item > div > ul > li > a > span {
    color: var(--text-color);
}

.card-title {
    color: var(--text-color) !important;
}

.ytcd-item > .header > #title {
    color: var(--text-color) !important; 
}

.ytcd-item > .content > span {
    color: var(--text-color) !important; 
}

ytcd-subscriber-highlights-item > .title {
    color: var(--text-color) !important; 
}

ytcd-subscriber-highlights-item > .subtitle {
    color: var(--text-color) !important; 
}

ytcd-subscriber-highlights-item > ul > li > ytcp-ve > a > div > span {
    color: var(--text-color) !important; 
}

ytcd-video-snapshot-item > div > .title{
    color: var(--text-color) !important; 
}

.content-text.ytcd-basic-item-large-image, .subscriber-sub-count.ytcd-subscriber-highlights-item, #published-time.metadata.style-scope.ytcp-post-snapshot, .section-subtitle-text.ytcd-channel-facts-item, .section-subtitle.section-subtitle-text.style-scope.ytcd-channel-facts-item, .subscribers-trend.ytcd-channel-facts-item{ /*Darker Text for descriptions*/
	color: var(--text-darken2-color) !important;
}

ytcp-icon-button.ytcp-quick-actions{ /*Quick action position*/
	background-color: var(--item-color);
    margin-top: 10px !important;
}

#dashboard-actions.ytcp-app{
    bottom: 10px !important;
}

#video-snapshot.yta-video-snapshot {
    background: none;
}

#video-snapshot.yta-video-snapshot > ytcp-ve > div {
    color: var(--text-darken2-color) !important; 

}

#video-snapshot.yta-video-snapshot > ytcp-ve > #metrics-table > div > #table-ranking-container > div > span {
    color: var(--text-color) !important;   
}

#video-snapshot.yta-video-snapshot > ytcp-ve > #metrics-table > div > #table-ranking-container > div > iron-icon {
    fill: var(--text-color) !important;    
}

.card, ytcp-entity-card{
	background-color: var(--bg2-color) !important;
	border-radius: 5px;
	border: 1px solid var(--border-color) !important;
	transition: .1s;
}

.card:hover, ytcp-entity-card:hover{
	background-color: #1e1e1e !important;
	transition: .1s;
}

ytcp-entity-card:hover{
    cursor: pointer;
}

.title-container.ytcp-thumbnail-with-title{
    background: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.8) 65%, #000);
    height: 60%;
}

.counter-label.ytcd-carousel-card{
    color: var(--text-color);
}

#subtitle.style-scope.ytcp-video-links-tooltip-body{
    color: var(--text-darken-color);
}

.video-position.style-scope.ytcp-video-links-tooltip-body{
    color: var(--text-darken-color);
}

.highlighted.ytcp-video-links-tooltip-body, .highlighted.ytcp-video-links-tooltip-body .video-position.ytcp-video-links-tooltip-body{
    color: var(--text-color);
}

.video-link.ytcp-video-links-tooltip-body:hover{
    color: var(--accent-color);
}

.green.yta-video-snapshot{
    color: var(--green-color) !important;
}

.style-scope.iron-icon[d="M12 22c5.52 0 10-4.48 10-10S17.52 2 12 2 2 6.48 2 12s4.48 10 10 10zm0-3.75l-4-3.99h3V7.25h2v7.01h3l-4 3.99z"]{
    fill: #aaa;
    fill-opacity: 1;
}

.trend-diff-up.ytcd-channel-facts-item{
    color: var(--green-color);
}

.trend-green.ytcp-trend-label{
    color: var(--green-color);
    --iron-icon-fill-color: var(--green-color);
}

.video-title.ytcd-video-list-cell-video-light{
    color: var(--text-color);
}

.metric.ytcd-video-list-cell-video-light:not(:last-of-type){
    color: var(--text-darken-color);
}

.metric.ytcd-video-list-cell-video-light{
    color: var(--text-darken-color);
}

.metric.ytcd-video-list-cell-video-light iron-icon.ytcd-video-list-cell-video-light{
    fill: var(--text-darken-color);
}

.content.ytcd-empty-inbox-item{
    color: var(--text-darken-color);
}

#row-container.row-highlighted.ytcd-video-list-cell-video-light, #row-container.row-focused.ytcd-video-list-cell-video-light{
    transition: .1s;
    background-color: var(--item-color);
}

.divider.ytcd-list-card{
    border-bottom: 1px solid var(--divider-color);
}

.divider.ytcd-help-center-issues-item{
    border-bottom: 1px solid var(--divider-color);
}

.ytcd-item > div > hr {
    border-bottom: 1px solid var(--divider-color) !important;
}

.divider.ytcd-post-stats-item{
    border-bottom: 1px solid var(--divider-color);
}

.divider.ytcd-list-card-collapsible{
    border-bottom: 1px solid var(--border-color);
}

#title.ytcp-video-links-tooltip-body{
    color: var(--text-color);
}

#content.ytcp-video-links-tooltip-body{
    color: var(--text-darken2-color);
}

.remove-default-style.style-scope.ytcp-video-links-tooltip-body{
    color: var(--text-darken-color);
}

.video-value.ytcp-video-links-tooltip-body{
    color: var(--text-darken-color);
}

.style-scope.iron-icon[d="M19 3H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm-1.99 6H7V7h10.01v2zm0 4H7v-2h10.01v2zm-3 4H7v-2h7.01v2z"]{
    fill: var(--text-darken-color) !important;
}

.row.ytcd-channel-facts-item:hover .left-col.ytcd-channel-facts-item .table-cell.ytcd-channel-facts-item{
    color: var(--text-color);
}

.row.ytcp-subscribers-table-row{ /*Subscriber list*/
    background-color: var(--bg2-color);
}

.row.ytcp-subscribers-table-row:hover{ /*Subscriber list*/
    background-color: var(--bg3-color);
    transition: .1s;
}

.divider.ytcp-subscribers-table{ /*Subscriber list*/
    border-bottom: 1px solid var(--border-color);
}

.subscriber-info-name.ytcp-subscribers-table-row, .subscriber-info.ytcp-subscribers-table-row a.ytcp-subscribers-table-row:hover .subscriber-info-name.ytcp-subscribers-table-row{ /*Subscriber list*/
    color: var(--text-color);
}

.subscriber-time-subscribed.ytcp-subscribers-table-row, .subscriber-sub-count.ytcp-subscribers-table-row{ /*Subscriber list*/
    color: var(--text-darken2-color);
}

ytcp-subscribe-button{ /*Subscriber list*/
	color: var(--accent-color);
}

ytcp-table-footer.ytcp-subscribers-table{ /*Subscriber list*/
    background-color: var(--bg3-color);
    border-bottom: 1px solid var(--border-color);
    border-top: 1px solid var(--border-color);
}

.ytcd-subscriber-list-dialog[slot="extension"] .subheader-text.ytcd-subscriber-list-dialog{ /*Subscriber list*/
    color: var(--text-darken2-color);
}

#dialog.ytcp-dialog .header.ytcp-dialog{ /*Subscriber list*/
    background-color: var(--bg3-color);
}

#page-title-container{ /*Widescreen fix*/
    max-width: 20000px !important;
}

.body.ytcp-subscribers-table{ /*Subscriber list*/
    background-color: var(--bg2-color);
}

ytcp-button.ytcd-inbox-item{
	color: var(--accent-color);
}

.strikes-align.ytcd-strikes-item{
    color: var(--text-darken2-color);
}

/*Analytics*/
paper-item.iron-selected.yta-key-metric-card {
   background-color: var(--item-color) !important;   
}

.metric-label.style-scope.yta-column-anomaly-tooltip{
	color: var(--accent-color);
}

.typical-performance-label.style-scope.yta-column-anomaly-tooltip{
    color: var(--text-color);
}

.typical-performance-value.style-scope.yta-column-anomaly-tooltip{
    color: var(--text-color);
}

.metric-value.style-scope.yta-column-anomaly-tooltip{
    color: var(--text-color);
}

.comparison-label.style-scope.yta-column-anomaly-tooltip{
    color: var(--text-color);
}

.style-scope.yta-key-metric-card{
    color: var(--bg2-color);
}

#card.layout.vertical.style-scope.yta-personalized-overview-middle-section{
    color: var(--text-color);
    background-color: var(--bg-color);
}

#card-hint.style-scope.yta-personalized-overview-middle-section{
    color: var(--text-darken-color);
}

#card-hint.style-scope.yta-personalized-overview-bottom-card{
    color: var(--text-darken-color);
}

.legend-text.style-scope.yta-key-metric-card{
    color: var(--text-darken-color);
}

