}

ytcp-dropdown-trigger[disabled] .container.ytcp-dropdown-trigger, ytcp-dropdown-trigger[disabled] iron-icon.ytcp-dropdown-trigger, ytcp-dropdown-trigger[disabled] .dropdown-trigger-text.ytcp-text-dropdown-trigger, ytcp-dropdown-trigger[disabled] .right-container.ytcp-dropdown-trigger iron-icon.ytcp-dropdown-trigger{
