    color: var(--item-color) !important;
}

textarea.iron-autogrow-textareaб #moderation-reason.ytcp-comment{
    color: var(--text-color);
}

.footer-container.ytcc-discovered-tab{
background-color: var(--bg-color);
}

#audio-library-link.ytcp-app{
    bottom: 40%;
}

ytmus-page{
    margin-top: 0px;
}

ytmus-player.ytmus-page{
    background-color: var(--bg2-color);
}


.track-info.ytmus-player, .progress-bar.ytmus-player{
