    border-radius: 10px;
}

.option-header.ytmonet-overview, .option-detail.ytmonet-overview, .container.ytcp-promo-page > [slot='headline'], #body.ytmerch-overview-short-flow, .contract-title.ytcp-contracts-settings, #label.ytcp-permissions-settings, .feature-name.yt-trust-tiers-features-level, .requirements-heading.yt-trust-tiers-features-level, .requirement-name.yt-trust-tiers-features-level, .relation-name.yt-trust-tiers-features-level{
