.text.ytcd-empty-news-item, a.ytcp-anchor, .container.ytcp-promo-page > [slot='subheading'], .body-text.ytpp-signup-overview, .ytpp-signup-overview[slot='body'] div.ytpp-signup-overview, .count.ytpp-signup-eligibility, .additional-info.ytpp-signup-overview, .user-name.ytcp-permissions-user-table, ytcp-contracts-settings[display-type="empty-for-channel"]{
