/* ==UserStyle==
@name         Material Dark GitHub
@version      4.0.6
@description  A Material Dark theme for GitHub
@namespace    CharlieEtienne
@author       CharlieEtienne
@homepageURL  https://github.com/CharlieEtienne/material-github
@supportURL   https://github.com/CharlieEtienne/material-github/issues
@updateURL    https://raw.githubusercontent.com/CharlieEtienne/material-github/master/material-github.user.css
@license      MIT
@advanced dropdown theme "Theme" {
  oceanic "Oceanic" 
<<<EOT 200, 19% EOT;
  darker "Darker" 
<<<EOT 0, 0% EOT;
  palenight "Palenight" 
<<<EOT 229, 20% EOT;
  custom "Custom Theme"
<<<EOT var(--hue, none), var(--saturation, none) EOT;
}
@var range hue "Custom theme hue" [0, 0, 360, 1, ""]
@var range saturation "Custom theme saturation" [100, "%"]
@var range lightness "Custom theme Lightness" [1, 0.1, 1.9, 0.1, ""]
@var range text_brightness "Text brightness" [1, 0.1, 1.9, 0.1, ""]
@advanced dropdown accent "Accent" {
	turquoise "Turquoise" <<<EOT 174 EOT;
	tomato "Tomato" <<<EOT 4 EOT;
	lavender "Lavender" <<<EOT 262 EOT;
	daisy "Daisy" <<<EOT 54 EOT;
	lime "Lime" <<<EOT 89 EOT;
	amethyst "Amethyst" <<<EOT 291 EOT;
	abyss "Abyss" <<<EOT 229 EOT;
	orange "Orange" <<<EOT 15 EOT;
	oceanic "Oceanic" <<<EOT 199 EOT;
	acid_lime "Acid lime" <<<EOT 73 EOT;
	dodgerblue "Dodgerblue" <<<EOT 218 EOT;
	strawberry "Strawberry" <<<EOT 340 EOT;
	light "Light" <<<EOT 0 EOT;
	indigo "Indigo" <<<EOT 231 EOT;
	water "Water" <<<EOT 207 EOT;
	sky "Sky" <<<EOT 180 EOT;
	plant "Plant" <<<EOT 123 EOT;
	aquamarine "Aquamarine" <<<EOT 166 EOT;
	cyan "Cyan" <<<EOT 187 EOT;
	palenight "Palenight" <<<EOT 231 EOT;
	amber "Amber" <<<EOT 51 EOT;
	atomic_purple "Atomic purple" <<<EOT 259 EOT;
	slate "Slate" <<<EOT 200 EOT;
	custom "Custom accent" <<<EOT var(--custom_accent, none) EOT;
}
@var range custom_accent "Custom accent hue" [0, 0, 360, 1, ""]
==/UserStyle== */
@-moz-document domain("github.com"), domain("render.githubusercontent.com") {
	:root{

		--hue: /*[[hue]]*/;
		--saturation: /*[[saturation]]*/;

		--bg1:  hsl(/*[[theme]]*/, calc(13% * /*[[lightness]]*/));
		--bg2:  hsl(/*[[theme]]*/, calc(15% * /*[[lightness]]*/));
		--bg3:  hsl(/*[[theme]]*/, calc(18% * /*[[lightness]]*/));
		--bg4:  hsl(/*[[theme]]*/, calc(20% * /*[[lightness]]*/));
		--bg5:  hsl(/*[[theme]]*/, calc(24% * /*[[lightness]]*/));
		--bg6:  hsl(/*[[theme]]*/, calc(27% * /*[[lightness]]*/));
		--bg7:  hsl(/*[[theme]]*/, calc(30% * /*[[lightness]]*/));
		--bg8:  hsl(/*[[theme]]*/, calc(33% * /*[[lightness]]*/));
		--bg9:  hsl(/*[[theme]]*/, calc(36% * /*[[lightness]]*/));
		--bg10: hsl(/*[[theme]]*/, calc(39% * /*[[lightness]]*/));
		--bg11: hsl(/*[[theme]]*/, calc(42% * /*[[lightness]]*/));

		--text0:  hsl(/*[[theme]]*/, calc(40% * /*[[text_brightness]]*/));
		--text1:  hsl(/*[[theme]]*/, calc(45% * /*[[text_brightness]]*/));
		--text2:  hsl(/*[[theme]]*/, calc(50% * /*[[text_brightness]]*/));
		--text3:  hsl(/*[[theme]]*/, calc(55% * /*[[text_brightness]]*/));
		--text4:  hsl(/*[[theme]]*/, calc(60% * /*[[text_brightness]]*/));
        	--text5:  hsl(/*[[theme]]*/, calc(65% * /*[[text_brightness]]*/));
		--text6:  hsl(/*[[theme]]*/, calc(70% * /*[[text_brightness]]*/));
		--text7:  hsl(/*[[theme]]*/, calc(75% * /*[[text_brightness]]*/));
		--text8:  hsl(/*[[theme]]*/, calc(80% * /*[[text_brightness]]*/));
        	--text9:  hsl(/*[[theme]]*/, calc(85% * /*[[text_brightness]]*/));
        	--text10: hsl(/*[[theme]]*/, calc(90% * /*[[text_brightness]]*/));
        	--text11: hsl(/*[[theme]]*/, calc(95% * /*[[text_brightness]]*/));

		--text-disabled: hsl(/*[[theme]]*/, calc(30% * /*[[text_brightness]]*/));

		--custom_accent: /*[[custom_accent]]*/;

		--selected:  hsl(/*[[accent]]*/, 38%, 66%);
		--selected2: hsl(/*[[accent]]*/, 38%, 55%);

		--btn:  hsl(/*[[accent]]*/, 38%, 40%);
		--btn2: hsl(/*[[accent]]*/, 29%, 33%);
		--btn3: hsl(/*[[accent]]*/, 21%, 22%);
		--btn4: hsl(/*[[accent]]*/, 25%, 15%);

       		/* CODE PALETTE (see https://www.material-theme.com/docs/reference/color-palette) */
        	--material-code-red:        #f07178;        /* Tags Color */
        	--material-code-teal:       #80cbc4;        /* Links Color */
        	--material-code-blue:       #82aaff;        /* Functions Color */
        	--material-code-cyan:       #89ddff;        /* Operators Color */
        	--material-code-green:      #c3e88d;        /* Strings Color */
        	--material-code-purple:     #c792ea;        /* Keywords Color */
        	--material-code-yellow:     #ffcb6b;        /* Attributes Color */
        	--material-code-orange:     #f78c6c;        /* Numbers & Parameters Color */
        	--material-code-comments:   var(--text2);    /* Comments Color */

		/* BACKGROUND */
		--color-bg-primary:                     var(--bg2);
		--color-bg-secondary:                   var(--bg2);
		--color-bg-canvas:                      var(--bg2);
		--color-bg-info:                        var(--bg4);
		--color-dropdown-bg:                    var(--bg2);
		--color-previewable-comment-form-bg:    var(--bg2);
		--color-auto-white:                     var(--bg3);
		--color-verified-badge-bg:              var(--bg3);
		--color-sidenav-selected-bg:            var(--bg4);
		--color-state-hover-secondary-bg:       var(--bg5);
		--color-bg-canvas-inset:                var(--bg1);
		--color-bg-canvas-inverse:              var(--bg3);
		--color-state-hover-primary-bg:         var(--selected2);

		/* BORDERS */
		--color-border-primary:                 var(--bg3);
		--color-border-secondary:               var(--bg3);
		--color-border-tertiary:                var(--bg4);
		--color-dropdown-border:                var(--bg5);
		--color-border-overlay:                 var(--bg5);

		/* SHADOWS */
		--color-autocomplete-shadow:            hsla(/*[[theme]]*/, calc(13% * /*[[lightness]]*/), 0.15) 0px 3px 6px;
		--color-shadow-large:                   hsla(/*[[theme]]*/, calc(13% * /*[[lightness]]*/), 0.2) 0px 8px 24px;
		--color-btn-focus-shadow:               hsla(/*[[accent]]*/, 38%, 66%, 0.3) 0px 0px 0px 3px;

		/* FOLDERS */
		--color-icon-folder:                    var(--selected2);
		--color-files-explorer-icon:		var(--selected2);

		/* TEXTS */
		--color-text-primary:                   var(--text7);
		--color-text-secondary:	                var(--text1);
		--color-verified-badge-text:            #2cbe87;
		--color-underlinenav-counter-text:      var(--text7);

		/* Notification System */
		--color-notifications-row-bg:			var(--bg5);
		--color-notifications-row-read-bg: 		var(--bg4);

		/* OTHER */
		--color-auto-gray-8:                    var(--text5);
		--color-auto-gray-9:                    var(--text7);
		--color-bg-info-inverse:                var(--btn);

	}

	textarea:focus {
		background-color: var(--bg1) !important;
	}
	body {
		background-color: var(--bg3) !important;
	}
	.markdown-body table tr, .blob-num-context, #user-content-toc td {
		background: var(--bg1) !important;
	}
	.highlight:not(.lines) tr:not(.inline-comments):hover td.selected-line.blob-num:not(.line-age), .highlight:not(.lines) tr:not(.inline-comments):hover td.blob-num, .blob-num[colspan], .blob-num-hunk {
		background: var(--bg3) !important;
	}
	.list-browser-item.closed, .content .verseblock-content, .content .verseblock-content, .simple-stacked-bar, .date_selector td.selectable_day:hover, .date_selector td.selectable_day.hover, .pagination a:hover, .pagination a:focus, .pagination span:hover, .pagination span:focus, .pagination em:not(.current):hover, .pagination em:not(.current):focus, .dropdown-divider, .js-current .standalone a, .sidebar-module .disable>a, .label-select-menu div.labelstyle-000000.selected, .ScreenshotCarousel-navitem.selected, .CircleBadge {
		background: var(--bg1) !important;
	}
	.header, .headers, #readme .markdown-body, .social-count, .file, .box-body, .markdown-body table tr:nth-child(2n), .steps li, .explore-section:nth-child(2n), .markdown-format table, .list-group-item, .chromed-list-browser .none p, .chromed-list-browser .error p, .list-browser-footer, .commandbar .choice:hover, #gollum-editor-help, .search-filter:hover, #wiki-history table tr:nth-child(2n) td, .display tr:hover, .octofication .message, .commandbar .loading:hover, .comment-content, .commandbar .message, .boxed-group-inner, #adv_code_search .search-page-label, .timeline-comment, #contributors-master svg.viz, .diff-table .line-comments, .menu, svg.spark, svg.capped-card-content, body.api .content tbody, .markdown-format img:not(.emoji), .mini-repo-list, #common-issues, .setup-wrapper .oauth-permissions-details, .milestone-list, .setup-form .setup-plans, .prose-diff > .markdown-body, .revision .gu, .tree-browser td, .tree-browser .alt td, .branches .branch-summary, .branches .no-results-message, .simple-box-footer, .commit-group, .blankslate, .content table tr, #search-results, table.files, .mergeable, .org-module, .org-add-footer, .branch-infobar, table.capped-list tr:nth-child(2n), .team-org-group, #gollum-editor-help-wrapper, .integrations-quality, .showcase-featured .mo-info .featured-grid-outer, .render-shell, .list, .latest-commit .commit-author, .bubble, .tabs > a.selected, .comment-form-head .tabnav-tab.selected, .oauth-org-access-details .boxed-group-list > li.on, #fullscreen_overlay, .branch-action-body, .inline-comment-form, .file-history-tease .participation, .gist-quicksearch-result-header, .label-generic, .migrate-org-roles, .commit-tease .commit-meta, .manage-repo-access-icon, .table-list:not(.team-member-list), .repo-file-upload-progress, .gcr-ext-editor-dialog, .shade-gray, .exploregrid-item, .signed-commit-badge-small, .facebox-popup, .explore-signup-entice-wrapper, .billing-addon-items tr.total-row, .billing-addon-items tr.dark-row, .Box-body, li.pinned-repo-item, .js-contribution-graph h2 + div:last-child, .project-triage-pane, .user-profile-nav.is-stuck, [data-filterable-for="projects-filter"] .container-lg, .review-summary-form-wrapper, #projects-results, .listgroup, .protected-branch-orgs-and-repo-admins, .listgroup-overflow, .Header, .project-column, .btn.btn-outline, .js-notice > .border, .news .issues_labeled .border, table.pricing-table, .two-factor-steps, div.border.rounded-1, article.border.rounded-1, .border-right.border-bottom.border-left, .graph-before-activity-overview, .access-form-wrapper, .project-header {
		background: var(--bg2) !important;
	}
	.table-list-header, .timeline-comment-header, .boxed-group > h3, .boxed-group .heading, .file-header, .capped-card h3, .select-menu-header, #gist-form .file .meta, .file .meta, .file-box .meta, .menu-heading, .audit-results-header, .branches .branch-group-heading, .auth-form-header, .commit-form, .migration-jumbotron, .migration-sub-header, .commit-tease, .manage-repo-access-title, .select-menu-divider, .signed-commit-header, .reorderable-task-lists .markdown-body .task-list-item.hovered, .next-charge-box, .credit-card, .branch-action-body .merge-message, .review-thread-reply, a.card.bg-gray-light:hover, .add-member-team-list .team-row-header, .Box-header, .pagination .gap, .pagination .disabled, .pagination .gap:hover, .pagination .disabled:hover, .listgroup-header, .merge-status-item, .previewable-comment-form .comment-form-head.tabnav, .bg-gray:not(body):not(.team-discussions-container), .Header .header-search-wrapper, .Header .header-search-wrapper input, .user-key-email-unverified {
		background: var(--bg1) !important;
	}
	.calendar-graph .contrib-details div, .tabnav-tabs a, .time, .gist-item .metadata .datetime, .list-group-item-meta, a.keyboard-shortcuts, button.keyboard-shortcuts, .simple-conversation-list, .simple-conversation-list > li .title .num, .drag-and-drop, .starred-repo .sort-info, .org-stats, .commandbar .choice, .commandbar .loading, .commandbar .description, ul.summary-stats li a, .authorship, .sha-block, .gh-header-meta, #last-edit, .setup-header .lead a, .repository-meta .edit-link a, .api-status a, .meta li, .who_when a, .numbers-summary a, .mini-icon-remove-close:hover, .section-heading-title .separator, .blog-aside .rss, .hook-list a, .github-jobs-promotion a.jobs-logo, .org-teams-list .team-meta, .repo-list-item .repo-description, .branch-infobar .lightweight-actions > li a, .repo-leaderboard-list .repo-leaderboard-meta-item a, .changed .moved-up:before, .changed .moved-down:before, .comment-header .octicon, a.section-heading-back, .list-group-item-meta .octicon, .repo-leaderboard-title .title-meta, a.pagehead-nav-item, .wiki-wrapper .wiki-empty-box, .commits-list-item .commits-comments-link, .timeline-comment-header, .commits-list-item .commit-meta, .issues-listing .table-list-issues .issue-comments-link, .issues-listing .table-list-issues .issue-meta-section a, a.issues-reset-query, .labels-list-action, table.tag-list p a, table.tag-list td.date a, .muted-link, a.muted-link, .render-view-modes li, .site-footer, .site-footer .octicon-mark-github:hover, .repo-list-stats .repo-list-stat-item, .range-editor span.flag .octicon, .commit-info .commit-meta a, .notifications .issue-notification.read .type-icon, .notifications .read a, .auth-form-header .octicon, .releases-tag-list p a, .history-link a, a.issue-nwo-link, ul.filter-list .filter-item, .member-meta .teams-link, .member-meta .btn-link, ul.comparison-list > li em, ul.comparison-list > li.title, .diffstat-summary, a.team-member-ellipsis, a.discussion-item-toggle-open, a.discussion-item-toggle-closed, button.discussion-item-toggle-open, button.discussion-item-toggle-closed, .dropdown-header, .participation a, .completeness-indicator-info, .type-icon-state-none, .pagehead-tabs-item, a.pagehead-tabs-item, .commit-tease, .reponav-item, a.reponav-item, .delete-owners-button, .stats-group-stat, .stats-group-stat.no-link:hover, .wiki-edit-link, .toolbar-item .menu-target, .member-meta .member-meta-link, .hidden-text-expander a, .ellipsis-expander, .text-muted, .listgroup-item, .discussion-item-header, .discussion-sidebar-heading, .org-header-meta .meta-item .meta-link, .underline-nav-item, .boxed-group-inner, .lead, .form-checkbox .note, .bot-identifier, .dashboard-notice .dismiss:hover, .text-gray, td.icon .octicon-file-text, td.icon .octicon-file-symlink-file, .UnderlineNav-item, .project-header-link, button.project-header-link, td.icon, .project-pane-close, .facebox-close, .tree-browser-result mark {
		color: rgba(255, 255, 255, calc(0.3 * /*[[text_brightness]]*/)) !important;
