 * |  STYLE render.githubusercontent.com
 * | ==========================================
 */
@-moz-document domain("render.githubusercontent.com"){
	body[data-render-url="https://render.githubusercontent.com"], #notebook {
		background-color: var(--bg3) !important;
	}
	p, li, table {
		color: var(--text7);
	}
	div.input_area, div.highlight pre, pre code {
		border: 1px solid var(--bg6);
