	}
	div.output_stderr {
		background-color: hsla(353, 25%, 21%, 1);
	}
	div.output_text{
		color: rgba(255, 255, 255, calc(0.7 * /*[[text_brightness]]*/));
		color: var(--text7);
	}
	code, pre, div.output_area pre {
		color: #B0BEC5 !important;
	}
	div.input_prompt{
		color: var(--material-code-blue);
	}
	div.output_prompt{
		color: var(--material-code-red);
	}
	.highlight .kn, 
	.highlight .k {
		color: var(--material-code-cyan);
	}
	.highlight .nn{
		color: var(--material-code-yellow);
	}
	.highlight .nf,
	.highlight .nb,
	.highlight .sb,
	.highlight .s{
		color: var(--material-code-red);
	}
	.highlight .o {
		color: var(--material-code-cyan);
	}
	.highlight .mi,
	.highlight .kc{
		color: var(--material-code-orange);
	}
	.highlight .s2,
	.highlight .s1{
		color: var(--material-code-green);
	}
	.highlight .si,
	.highlight .ow {
		color: var(--material-code-purple);
	}
	.highlight .mf,
	.highlight .nv{
		color: var(--material-code-blue);
	}
	.highlight .c1,
	.highlight .sd,
	.highlight .m{
		color: var(--material-code-comments);
	}
	img.math {
		-webkit-filter: invert(.75);
		filter: invert(.75);
	}
}

