.platform-win #user-settings .side-8zPYf6 .item-PXvHYJ:nth-child(6)::before {
    background: url(https://codedotspectra.github.io/themes/icons/billing.png) center;
}
/*Discord Nitro Icon*/
[aria-label="USER_SETTINGS"] .sidebar-CFHs9e .item-PXvHYJ:nth-child(8):before {
    background: url(https://codedotspectra.github.io/themes/icons/nitro.png) center;
}
/*Server Boost Icon*/
[aria-label="USER_SETTINGS"] .sidebar-CFHs9e .item-PXvHYJ:nth-child(10):before {
    background: url(https://codedotspectra.github.io/themes/icons/boost.png) center;
}
/*HypeSquad Icon*/
[aria-label="USER_SETTINGS"] .sidebar-CFHs9e .item-PXvHYJ:nth-child(11):before {
    background: url(https://codedotspectra.github.io/themes/icons/hype.png) center;
}
/*Appearance Icon*/
.platform-win #user-settings .side-8zPYf6 .item-PXvHYJ:nth-child(20)::before {
    background: url(https://codedotspectra.github.io/themes/icons/eye.png) center;
}
/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/*---------DM List---------*/
/*Transition Settings for Hover/Select*/
.categoryItem-3zFJns .itemInner-3gVXMG,
.clickable-1JJAn8,
.layout-2DM8Md {
    transition: all 200ms ease;
    /*Sets transition settings for below*/
}
/*Hover/Select User*/
.categoryItem-3zFJns.selectedCategoryItem-3X8ujp .itemInner-3gVXMG,
.clickable-1JJAn8:hover .layout-2DM8Md,
.selected-aXhQR6 .layout-2DM8Md {
    position: relative;
    padding-left: 15px/*Adds transition/movement when hovering/select user in DM List*/
}
/*-------------------------*/
/*------Option Boxes in Settings Pages------*/
/*Check Box/Circle*/
.cardPrimaryEditable-3KtE4g .checkboxWrapper-SkhIWG .checkbox-1ix_J3.checked-3_4uQ9,
.theme-dark .checkbox-1ix_J3 {
    border-radius: 15px;
    /*Turns the pretty much all check boxes in existence into a check circle (yes, even outside the settings page)*/
}
.theme-dark .checked-3_4uQ9 {
    border-color: transparent !important;
    /*Removes the border of the check circle (or box) when selected in
  Safe Direct Messaging*/
}

/*Selection Box*/
.theme-dark .card-3Qj_Yx {
    border-radius: 21px;
    /*makes the boxes more rounded on the edges. max roundess: 21px*/
}
/*------------------------------------------*/
/*-------Follow Channels Modal-------*/
/*Drop Down Menu*/
.css-3vaxre-menu {
    background-color: rgb(0, 0, 0);
    /*makes the dropdown menu background black and thus the text easier to read*/
    /*might want to change depending on theme*/
    border-color: transparent;
    /*gets rid of the border*/
    border-radius: 15px;
    /*just in case you want a border, it looks nice and rounded*/
}


/*Send To & Select Channel Boxes*/
/*No Selection*/
.css-gvi9bl-control {
    border-color: rgba(var(--main-color-numerical), 0.6);
    border-radius: 15px;
    /*Not hovering*/
}
.css-gvi9bl-control:hover {
    border-color: var(--main-color);
    /*Hovering*/
}

/*No Drop Down Menu*/
.css-17e1tep-control:hover {
    border-color: var(--main-color);
    border-radius: 15px;
}
.css-17e1tep-control {
    border-radius: 15px;
    border-color: rgba(var(--main-color-numerical), 0.6);
}

/*Drop Down Menu*/
.css-6fzn47-control {
    border-radius: 15px;
    border-color: rgba(var(--main-color-numerical), 0.6);
}
.css-6fzn47-control:hover {
    border-color: var(--main-color);
    border-radius: 15px;
}
/*-------------------------------------------------*/
/*-----Selected Channel-----*/
.wrapper-2jXpOf.modeSelected-346R90 .content-1x5b-n:after {
    background: rgba(var(--main-color-numerical), 0.3);
    border: 2px solid rgba(var(--main-color-numerical), 0.7);
    border-radius: 7px;
    /*max: 15px*/
    /*removes transitions*/
    -webkit-animation: cv-channel-select none!important;
    animation: cv-channel-select none!important;
    -webkit-transition: none!important;
}
/*--------------------------*/
/*-------Selected DM-------*/
.privateChannels-1nO12o .channel-2QD9_O.selected-aXhQR6 .layout-2DM8Md:after {
    background: rgba(var(--main-color-numerical), 0.3);
    border: 2px solid rgba(var(--main-color-numerical), 0.7);
    border-radius: 7px;
    /*max: 15px*/
    animation: none;
    -webkit-transition: none;
    /*removes animation thing when selecting DM's. might be scuffed*/
}
/*-------------------------*/
/*-------Bottom Left Profile Tab Optimization-------*/
.container-3baos1 {
    padding-top: 3px;
    padding-bottom: 3px;
    /*Adds spacing upwards and downwards*/
    border-radius: 4px;
    /*Rounds the edges a bit if we need it*/
}
/*--------------------------------------------------*/
/*--------Home Button Optimization--------*/
/*Bar on Top*/
.pill-2uzAFe.wrapper-sa6paO {
    display: none;
    /*Removes the bar on top*/
}
/*Background Behind Icon*/
.wrapper-1BJsBx.selected-bZ3Lue .childWrapper-anI2G9 {
    background-color: rgb(var(--main-color-numerical), 0);
    /*Removes any existing background color that appears*/
}
.wrapper-1BJsBx:hover .childWrapper-anI2G9 {
    background-color: rgb(var(--main-color-numerical));
    /*re-adds background color ONLY when hovering*/
}
/*----------------------------------------*/
/*-----Subsetting Select in User Settings (including BD Settings)-----*/
.standardSidebarView-3F1I7i .sidebar-CFHs9e .side-8zPYf6 .item-PXvHYJ.selected-3s45Ha:after,
.standardSidebarView-3F1I7i .sidebar-CFHs9e .side-8zPYf6 .item-PXvHYJ[style*="color: rgb(255, 255, 255)"]:after {
    background: rgba(var(--main-color-numerical), 0.3);
    border: 2px solid rgba(var(--main-color-numerical), 0.7);
    border-radius: 7px;
    animation: none;
    -webkit-transition: none;
    /*keeps it exact same as select DM's*/
}

.side-8zPYf6 #bd-settings-sidebar .ui-tab-bar-item.selected:after {
    background: rgba(var(--main-color-numerical), 0.3);
    border: 2px solid rgba(var(--main-color-numerical), 0.7);
    border-radius: 7px;
    animation: none;
    -webkit-transition: none;
    /*for bd settings specifically*/
}
/*--------------------------------------------------------------------*/
/*-------Account Information Blur-------*/
.colorHeaderPrimary-26Jzh- {
    filter: blur(5px);
    transition: 300ms;
}
.colorHeaderPrimary-26Jzh-:hover {
    filter: none;
}
/*-----------------------------------------*/
/*-----Check box/circle thingy mabob when uploading files-----*/
.checkbox-1ix_J3.checked-3_4uQ9[style*="border-color: rgb(114, 137, 218)"] {
    background-color: var(--main-color) !important;
    /*adds background color to "Mark as spoiler" check box/circle when checked*/
}
/*------------------------------------------------------------*/
/*-------Blur spoiler-------*/
/*blurs spoiler text instead of having a black bar over it*/
.contents-2mQqc9 .spoilerText-3p6IlD.hidden-HHr2R9,
.contents-2mQqc9 .spoilerText-3p6IlD.hidden-HHr2R9:hover,
.contents-2mQqc9 .spoilerText-3p6IlD {
    background: rgb(130, 130, 130, 0.1);
    /*adds minor grey-ish filter to differentiate spoilted text from normal text*/
    border-radius: 7px;
    /*adds rounded edges for the filter described above*/
}
.contents-2mQqc9 .spoilerText-3p6IlD.hidden-HHr2R9 > .inlineContent-3ZjPuv {
    filter: blur(3px);
    /*adds blur filter when text is left alone*/
    opacity: 1;
    /*makes blurred text fully opaque*/
}
.contents-2mQqc9 .spoilerText-3p6IlD.hidden-HHr2R9:hover > .inlineContent-3ZjPuv {
    filter: none;
    /*removes blur filter on hover*/
    opacity: 1;
    /*makes text opacity look like normal, non-spoiled text on hover*/
}
/*--------------------------*/
/*-----Selected Tab Button in Friends Section-----*/
.container-1r6BKw .topPill-30KHOu .item-PXvHYJ.selected-3s45Ha.themed-OHr7kt {
    background-color: rgb(var(--main-color-numerical), 0.3) !important;
    border: 2px solid rgb(var(--main-color-numerical), 0.7);
}
/*------------------------------------------------*/
/*-----Connections-----*/
.connection-1fbD7X,
.connectionHeader-2MDqhu {
    background-color: rgb(130, 130, 130, 0.1);
    /*makes the background for each acc connection partially transparent*/
}
