/**
 * @name Comfy-theme
 * @author Nyria#0001
 * @description A simple customisable comfy theme
 * @version 1.0.0
 */
@import "https://nyri4.github.io/Comfy-theme/BetterDiscord/main.css";

:root {

	/* -- Addons -- */
	/* BetterAvatar Popout */
	--popout-status-icon: none;
	/* Set it to "block" if u want to have the status icon in big popouts */
	--popout-status-mask: none;
	/* Set it to "url(#svg-mask-avatar-status-round-80)" if u want to enable the avatar mask in big popouts */
	--avatar-radius: 10%;
	/* Change the roundness of avatar in big popouts, u need to disable the mask to use this */
	/* Colored emoji picker */
	--colored-emoji: grayscale(0%);
	/* Change the value to "100%" if u want the basic one */
	/* -- Others settings -- */
	/* User settings color (Mute, Deafen and settings) */
	--user-buttons-color: #096dc0;

	/* User settings spacing */
	--user-buttons-spacing: 10px;

	/* Chat buttons color */
	--chat-buttons: #6E85D3;

	/* Circles next to role names */
	--role-circle: 7px;
	/* Set it to "0px" if u don't want circles */
	/* Tooltips (Example : https://i.ibb.co/hDSQtqw/Capture.png) */
	--tooltips: block;
	/* Set it to "none" if u don't want it */
	/* Separators */
	--separators: none;
	/* Set it to "block" if u want it */
	/* Discord logo */
	--discord-logo: none;
	/* Set it to "block" if u want it */
}
/* thx snapperito and Harley for the help :3 */
.theme-dark {
	--background-tertiary: #101320 !important;
	--background-secondary: #1e2233 !important;
	--background-secondary-alt: #191f2e !important;
	--background-primary: #23283d !important;
	--channeltextarea-background: #191f2e !important;
	--background-accent: #6E85D3 !important;
	--background-modifier-hover: #1c2030 !important;
	--background-modifier-active: #1a1e2e !important;
	--background-modifier-selected: #191f2e !important;
	--deprecated-card-bg: #12141f63 !important;
	--background-floating: #101320 !important;
	--deprecated-quickswitcher-input-background: #101320 !important;
	--scrollbar-auto-thumb: #121722 !important;
	--scrollbar-auto-track: #191f2e !important;
	--scrollbar-thin-thumb: #141925 !important;
