/**
 * @name Pop!
 * @author Devin#2078
 * @version 0.1.0
 * @description Makes UI elements 'pop out' on hover.
 * @source https://github.com/devinkid1/Pop
*/

/* Imports */

@import url("https://devinkid1.github.io/Pop/base.css");

/* Settings */

:root {
  /* Hover Scale */

  --HoverScale: 1.025;

  /* Transition Time */

  --TransitionTime: 0.25s;

  /* Ease Type */

  --EaseType: cubic-bezier(0, 0.75, 0, 1.75);
}

