		stroke: #fff;
	}
	@media (min-width: 768px) {
		.nav-desktop-productDropdown, .nav-desktop-langDropdown {
			background: var(--bg4);
		}
	}
	.ais-SearchBox-input {
		background: var(--bg1) url(/assets/images/octicons/search.svg) no-repeat 6px;
		background-blend-mode: luminosity;
	}
	.form-control, .ais-SearchBox-input, .form-select {
		background-color: var(--bg1);
		color: rgba(255, 255, 255, calc(0.7 * /*[[text_brightness]]*/));
		color: var(--text7);
	    border: 1px solid var(--bg6);
	}
	.form-control.focus, .focus.ais-SearchBox-input, .form-control:focus, .ais-SearchBox-input:focus, .form-select.focus, .form-select:focus {
		border-color: var(--btn);
		outline: none;
		box-shadow: 0 0 0 3px hsla(/*[[accent]]*/, 38%, 66%, 0.3);
	}
	.ais-Hits-item:hover {
		background: var(--bg5);
	}
	.markdown-body .lead-mktg p {
		color: rgba(255, 255, 255, calc(0.7 * /*[[text_brightness]]*/));
		color: var(--text7);
	}

	.hljs {
		color: rgba(255, 255, 255, calc(0.6 * /*[[text_brightness]]*/));
		color: var(--text5);
	}
	.hljs-tag, .hljs-name, .hljs-attribute {
		color: var(--material-code-blue);
	}
	.hljs-number, .hljs-literal, .hljs-variable, .hljs-template-variable, .hljs-tag .hljs-attr {
		color: var(--material-code-red);
	}
	.hljs-string, .hljs-doctag {
		color: var(--material-code-green);
	}

	.border.bg-purple-light, div.border.rounded-1.bg-purple-light {
		background-color: hsla(260, 100%, 30%, 40%) !important;
	}
	.border.warning, div.border.rounded-1.warning {
		background-color: hsla(30, 57%, 32%, 40%) !important;
	}
	.border.border-purple {
		border-color: hsl(260 51% 30%) !important;
	}

	@media (min-width: 768px){
		header #search-results-container {
			background: var(--bg3);
		}
	}
	.link-with-intro-title {
		color: var(--selected) !important;
	}
}

/**
 * | ==========================================
 * |  STYLE render.githubusercontent.com
 * | ==========================================
 */
@-moz-document domain("render.githubusercontent.com"){
	body[data-render-url="https://render.githubusercontent.com"], #notebook {
		background-color: var(--bg3) !important;
	}
	p, li, table {
		color: var(--text7);
	}
	div.input_area, div.highlight pre, pre code {
		border: 1px solid var(--bg6);
		background-color: var(--bg4) !important;
	}
	div.output_stderr {
		background-color: hsla(353, 25%, 21%, 1);
	}
	div.output_text{
		color: rgba(255, 255, 255, calc(0.7 * /*[[text_brightness]]*/));
		color: var(--text7);
	}
	code, pre, div.output_area pre {
		color: #B0BEC5 !important;
	}
	div.input_prompt{
		color: var(--material-code-blue);
	}
	div.output_prompt{
		color: var(--material-code-red);
	}
	.highlight .kn, 
	.highlight .k {
		color: var(--material-code-cyan);
	}
	.highlight .nn{
		color: var(--material-code-yellow);
	}
	.highlight .nf,
	.highlight .nb,
	.highlight .sb,
	.highlight .s{
		color: var(--material-code-red);
	}
	.highlight .o {
		color: var(--material-code-cyan);
	}
	.highlight .mi,
	.highlight .kc{
		color: var(--material-code-orange);
	}
	.highlight .s2,
	.highlight .s1{
		color: var(--material-code-green);
	}
	.highlight .si,
	.highlight .ow {
		color: var(--material-code-purple);
	}
	.highlight .mf,
	.highlight .nv{
		color: var(--material-code-blue);
	}
	.highlight .c1,
	.highlight .sd,
	.highlight .m{
		color: var(--material-code-comments);
	}
	img.math {
		-webkit-filter: invert(.75);
		filter: invert(.75);
	}
}

