  }
  .newUI .entry .svg-icon {
    fill: #64b5f6;
  }
  .newUI .entry:hover .svg-icon {
    fill: #ddd;
  }
  .newUI .entry:hover .svg-icon:hover {
    fill: #fff;
  }
  .entry .svg-icon {
    fill: #64b5f6;
  }
  .entry:hover .svg-icon:hover {
