  }
  .cm-s-default .CodeMirror-selected {
    background: #0d47a1 !important;
  }
  .cm-s-default .colorview-swatch::after {
    border-color: #aaa;
  }
  .CodeMirror.cm-s-default pre {
    color: #ddd;
  }
  .cm-s-default .cm-qualifier {
