			top: 60px;
			left: 300px;
			padding: 0;
		}

		/* installed list */
		#stylus-manage #installed {
			display: block !important;
			position: absolute;
			top: 60px;
			padding-left: 0;
			padding-bottom: 10px;
			margin: 0 !important;
			width: 100%;
			height: calc(100vh - 60px);
			overflow-y: scroll;
		}

		/* balance of width : style-name and applies-to */
		.newUI .style-name {
			width: 60%;
		}

		.newUI .target {
			max-width: calc(40vw - var(--actions-width)) !important;
		}
