  --webkit-scrollbar-track-hover: /*[[custom-track-color-hover]]*/;
  --webkit-scrollbar-width-height: /*[[webkit-scrollbar-width-height]]*/;
  --webkit-scrollbar-border-radius: /*[[webkit-scrollbar-border-radius]]*/;
}
